import React, { useState, useEffect } from 'react';

function AvatarPage() {
  const [avatarUrl, setAvatarUrl] = useState("");
  const [loading, setLoading] = useState(true);

  // Fetch the Ready Player Me URL from the backend
  useEffect(() => {
    fetch("http://127.0.0.1:5000/custom-avatar")
      .then(res => res.json())
      .then(data => {
        setAvatarUrl(data.avatarUrl);
        setLoading(false);
      })
      .catch(err => {
        console.error("Error fetching avatar URL:", err);
        setLoading(false);
      });
  }, []);

  return (
    <div className="page-container">
      <h2>Customizable Avatar</h2>
      <p>Create and adjust your digital avatar below:</p>
      {loading ? (
        <p>Loading avatar customization...</p>
      ) : avatarUrl ? (
        <iframe
          title="Custom Avatar"
          src={avatarUrl}
          width="100%"
          height="600px"
          style={{ border: 'none', borderRadius: '10px' }}
        />
      ) : (
        <p>Error loading avatar widget.</p>
      )}
    </div>
  );
}

export default AvatarPage;
